import React, { useEffect, useState } from 'react';
import { db, auth } from './firebase';
import {
  collection,
  addDoc,
  deleteDoc,
  doc,
  onSnapshot,
  query,
  where,
  orderBy,
  updateDoc,
} from 'firebase/firestore';

function ToDoList() {
  const [tasks, setTasks] = useState([]);
  const [input, setInput] = useState('');
  const user = auth.currentUser;

  useEffect(() => {
    if (!user) return;
    const q = query(
      collection(db, 'tasks'),
      where('uid', '==', user.uid),
      orderBy('createdAt', 'desc')
    );
    const unsubscribe = onSnapshot(q, (snapshot) => {
      setTasks(snapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      })));
    });
    return unsubscribe;
  }, [user]);

  const handleAddTask = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;
    await addDoc(collection(db, 'tasks'), {
      text: input,
      uid: user.uid,
      createdAt: new Date(),
      completed: false
    });
    setInput('');
  };

  const handleDeleteTask = async (id) => {
    await deleteDoc(doc(db, 'tasks', id));
  };

  const toggleComplete = async (id, current) => {
    await updateDoc(doc(db, 'tasks', id), { completed: !current });
  };

  return (
    <div>
      <form onSubmit={handleAddTask} style={{ marginBottom: 16 }}>
        <input
          value={input}
          onChange={e => setInput(e.target.value)}
          placeholder="Add a task..."
          style={{ width: '70%', padding: 8 }}
        />
        <button type="submit" style={{ padding: 8, marginLeft: 8 }}>
          Add
        </button>
      </form>



      <ul style={{ listStyle: 'none', padding: 0 }}>
        {tasks.map(task => (
          <li key={task.id} style={{
            margin: '8px 0',
            display: 'flex',
            alignItems: 'center',
            opacity: task.completed ? 0.5 : 1,
            textDecoration: task.completed ? 'line-through' : 'none',
          }}>
            <input
              type="checkbox"
              checked={task.completed}
              onChange={() => toggleComplete(task.id, task.completed)}
              style={{ marginRight: 8 }}
            />
            <span style={{ flex: 1 }}>{task.text}</span>
            <button onClick={() => handleDeleteTask(task.id)} style={{ marginLeft: 8 }}>Delete</button>
          </li>
        ))}
      </ul>
      {tasks.length === 0 && <div>No tasks yet. Add one above!</div>}
    </div>
  );
}

export default ToDoList;
